# April 5, 2021# Ben Zaloga & Kayla Albano# STAT 1129 - Final Project# Rock, Paper, Scissorsimport randomoptions = ['r', 'p', 's']playerscore = 0computerscore = 0def start():    ready = input('Are you ready to play? (y / n): ').lower()    if ready == 'y':        randomfunc(playerscore, computerscore)    elif ready == 'n':        print('Come back soon!')    else:        print('Invalid selection. Please try again!')        start()        def randomfunc(playerscore, computerscore):    computer = random.choice(options)    valid(computer, playerscore, computerscore)    def valid(computer, playerscore, computerscore):    player = input('Make a move (r / p / s): ').lower()    if player in options:        if computer == 'r':            print('Computer chose ... ROCK!')        elif computer == 'p':            print('Computer chose ... PAPER!')        elif computer == 's':            print('Computer chose ... SCISSORS!')        result(player, computer, playerscore, computerscore)    else:        print('Invalid selection. Please try again.')        valid(computer, playerscore, computerscore)def result(player, computer, playerscore, computerscore):    if player == computer:        print('Tie!')    elif player == 'r':        if computer == 'p':            print('You lose!')            computerscore += 1        else:            print('You won!')            playerscore += 1    elif player == 'p':        if computer == 'r':            print('You won!')            playerscore += 1        else:            print('You lose!')            computerscore += 1    elif player == 's':        if computer == 'r':            print('You lose!')            computerscore += 1        else:            print('You won')            playerscore += 1    score(playerscore, computerscore)def score(playerscore, computerscore):    print('Player:', playerscore, '| Computer:', computerscore)    playagain(playerscore, computerscore)    def playagain(playerscore, computerscore):    again = input('Play Again? (y / n): ').lower()    if again == 'y':        randomfunc(playerscore, computerscore)    elif again == 'n':        print('Thanks for Playing! \nFinal Score: \nPlayer:', playerscore, '| Computer:', computerscore)    else:        print('Invalid selection. Please try again.')        playagain(playerscore, computerscore)    def main():    print('Welcome to Rock, Paper, Scissors!')    start()    if __name__ == "__main__":    main()